-- Fix Foreign Key Constraints to Enable Cascading Deletes for Customer Deletion -- Run this script in MySQL Workbench or command line
USE full-stack-ecommerce;
-- Step 1: Find all foreign key constraints referencing customers table SELECT TABLE_NAME, COLUMN_NAME, CONSTRAINT_NAME, REFERENCED_TABLE_NAME, REFERENCED_COLUMN_NAME FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE WHERE REFERENCED_TABLE_NAME = 'customers' AND TABLE_SCHEMA = 'full-stack-ecommerce';
-- Step 2: Show current table structures to see constraint names SHOW CREATE TABLE carts;
-- Step 3: Fix the most likely constraint - carts.customer_id -- Try the most common constraint names (one of these should work):
-- Option A: Common auto-generated name SET @sql = NULL; SELECT CONCAT('ALTER TABLE carts DROP FOREIGN KEY ', CONSTRAINT_NAME, ';') INTO @sql FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE WHERE TABLE_NAME = 'carts' AND COLUMN_NAME = 'customer_id' AND REFERENCED_TABLE_NAME = 'customers' AND TABLE_SCHEMA = 'full-stack-ecommerce' LIMIT 1;
-- Execute the drop statement if constraint exists SET @sql = IFNULL(@sql, 'SELECT "No carts foreign key found" as message;'); PREPARE stmt FROM @sql; EXECUTE stmt; DEALLOCATE PREPARE stmt;
-- Add the new constraint with CASCADE ALTER TABLE carts ADD CONSTRAINT fk_carts_customer_cascade FOREIGN KEY (customer_id) REFERENCES customers(customer_id) ON DELETE CASCADE ON UPDATE CASCADE;
-- Step 4: Check if cart_items also directly references customers -- (Some designs have cart_items -> customers, others go cart_items -> carts -> customers) SELECT COUNT(*) as cart_items_references_customers FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE WHERE TABLE_NAME = 'cart_items' AND COLUMN_NAME = 'customer_id' AND REFERENCED_TABLE_NAME = 'customers' AND TABLE_SCHEMA = 'full-stack-ecommerce';
-- If cart_items directly references customers, fix that too SET @sql2 = NULL; SELECT CONCAT('ALTER TABLE cart_items DROP FOREIGN KEY ', CONSTRAINT_NAME, ';') INTO @sql2 FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE WHERE TABLE_NAME = 'cart_items' AND COLUMN_NAME = 'customer_id' AND REFERENCED_TABLE_NAME = 'customers' AND TABLE_SCHEMA = 'full-stack-ecommerce' LIMIT 1;
-- Execute if constraint exists SET @sql2 = IFNULL(@sql2, 'SELECT "No cart_items->customers foreign key found" as message;'); PREPARE stmt2 FROM @sql2; EXECUTE stmt2; DEALLOCATE PREPARE stmt2;
-- Add cascade constraint for cart_items if it was referencing customers directly -- Note: This will only run if there was actually a constraint to drop SET @add_cart_items_fk = ( SELECT COUNT(*) FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'cart_items' AND COLUMN_NAME = 'customer_id' AND TABLE_SCHEMA = 'full-stack-ecommerce' );
-- Only add if customer_id column exists in cart_items SET @sql3 = CASE WHEN @add_cart_items_fk > 0 THEN 'ALTER TABLE cart_items ADD CONSTRAINT fk_cart_items_customer_cascade FOREIGN KEY (customer_id) REFERENCES customers(customer_id) ON DELETE CASCADE ON UPDATE CASCADE;' ELSE 'SELECT "cart_items does not directly reference customers - this is normal" as message;' END;
PREPARE stmt3 FROM @sql3; EXECUTE stmt3; DEALLOCATE PREPARE stmt3;
-- Step 5: Verify the changes SELECT 'After changes:' as status, TABLE_NAME, COLUMN_NAME, CONSTRAINT_NAME, REFERENCED_TABLE_NAME, REFERENCED_COLUMN_NAME FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE WHERE REFERENCED_TABLE_NAME = 'customers' AND TABLE_SCHEMA = 'full-stack-ecommerce';
-- Show the new constraint details SELECT CONSTRAINT_NAME, UPDATE_RULE, DELETE_RULE FROM INFORMATION_SCHEMA.REFERENTIAL_CONSTRAINTS WHERE CONSTRAINT_SCHEMA = 'full-stack-ecommerce' AND REFERENCED_TABLE_NAME = 'customers';
SELECT 'Foreign key constraints have been updated to CASCADE on DELETE' as result;

package com.assessment.demo.controllers;

import com.assessment.demo.dao.VacationRepository;
import com.assessment.demo.entities.Vacation;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

@CrossOrigin(origins = "http://localhost:4200")
@RestController
@RequestMapping("/api/vacations")
public class VacationController {

    private final VacationRepository vacationRepository;

    @Autowired
    public VacationController(VacationRepository vacationRepository) {
        this.vacationRepository = vacationRepository;
    }

    // GET all vacations with _embedded structure for frontend compatibility
    @GetMapping(produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Map<String, Object>> getAllVacations() {
        List<Vacation> vacations = vacationRepository.findAll();
        Map<String, Object> response = new HashMap<>();
        Map<String, Object> embedded = new HashMap<>();
        embedded.put("vacations", vacations);
        response.put("_embedded", embedded);
        return ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(response);
    }

    // GET single vacation by ID
    @GetMapping(value = "/{id}", produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Vacation> getVacationById(@PathVariable Long id) {
        return vacationRepository.findById(id)
                .map(vacation -> ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(vacation))
                .orElse(ResponseEntity.notFound().build());
    }

    // POST new vacation
    @PostMapping(consumes = MediaType.APPLICATION_JSON_VALUE, produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Vacation> createVacation(@RequestBody Vacation vacation) {
        try {
            Vacation savedVacation = vacationRepository.save(vacation);
            return ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(savedVacation);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    // PUT update existing vacation
    @PutMapping(value = "/{id}", consumes = MediaType.APPLICATION_JSON_VALUE, produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Vacation> updateVacation(@PathVariable Long id, @RequestBody Vacation vacationDetails) {
        return vacationRepository.findById(id)
                .map(vacation -> {
                    vacation.setVacation_title(vacationDetails.getVacation_title());
                    vacation.setDescription(vacationDetails.getDescription());
                    vacation.setTravel_price(vacationDetails.getTravel_price());
                    vacation.setImage_URL(vacationDetails.getImage_URL());
                    Vacation updatedVacation = vacationRepository.save(vacation);
                    return ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(updatedVacation);
                })
                .orElse(ResponseEntity.notFound().build());
    }

    // DELETE vacation
    @DeleteMapping("/{id}")
    public ResponseEntity<?> deleteVacation(@PathVariable Long id) {
        return vacationRepository.findById(id)
                .map(vacation -> {
                    vacationRepository.delete(vacation);
                    return ResponseEntity.ok().build();
                })
                .orElse(ResponseEntity.notFound().build());
    }
}

package com.assessment.demo.controllers;

import com.assessment.demo.dao.VacationRepository;
import com.assessment.demo.entities.Vacation;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpHeaders;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

@CrossOrigin(origins = "http://localhost:4200")
@RestController
@RequestMapping("/api/vacations")
public class VacationController {

    private final VacationRepository vacationRepository;

    @Autowired
    public VacationController(VacationRepository vacationRepository) {
        this.vacationRepository = vacationRepository;
    }

    // GET all vacations with _embedded structure for frontend compatibility
    @GetMapping(produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Map<String, Object>> getAllVacations() {
        List<Vacation> vacations = vacationRepository.findAll();
        Map<String, Object> response = new HashMap<>();
        Map<String, Object> embedded = new HashMap<>();
        embedded.put("vacations", vacations);
        response.put("_embedded", embedded);

        HttpHeaders headers = new HttpHeaders();
        headers.add("Access-Control-Allow-Origin", "*");
        headers.add("Access-Control-Allow-Headers", "*");

        return ResponseEntity.ok().headers(headers).contentType(MediaType.APPLICATION_JSON).body(response);
    }

    // GET single vacation by ID
    @GetMapping(value = "/{id}", produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Vacation> getVacationById(@PathVariable Long id) {
        return vacationRepository.findById(id)
                .map(vacation -> ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(vacation))
                .orElse(ResponseEntity.notFound().build());
    }

    // POST new vacation
    @PostMapping(consumes = MediaType.APPLICATION_JSON_VALUE, produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Vacation> createVacation(@RequestBody Vacation vacation) {
        try {
            Vacation savedVacation = vacationRepository.save(vacation);
            return ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(savedVacation);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    // PUT update existing vacation
    @PutMapping(value = "/{id}", consumes = MediaType.APPLICATION_JSON_VALUE, produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Vacation> updateVacation(@PathVariable Long id, @RequestBody Vacation vacationDetails) {
        return vacationRepository.findById(id)
                .map(vacation -> {
                    vacation.setVacation_title(vacationDetails.getVacation_title());
                    vacation.setDescription(vacationDetails.getDescription());
                    vacation.setTravel_price(vacationDetails.getTravel_price());
                    vacation.setImage_URL(vacationDetails.getImage_URL());
                    Vacation updatedVacation = vacationRepository.save(vacation);
                    return ResponseEntity.ok().contentType(MediaType.APPLICATION_JSON).body(updatedVacation);
                })
                .orElse(ResponseEntity.notFound().build());
    }

    // DELETE vacation
    @DeleteMapping("/{id}")
    public ResponseEntity<?> deleteVacation(@PathVariable Long id) {
        return vacationRepository.findById(id)
                .map(vacation -> {
                    vacationRepository.delete(vacation);
                    return ResponseEntity.ok().build();
                })
                .orElse(ResponseEntity.notFound().build());
    }
}
